:root {
  --tour-x-distance: 42px;
  --tour-anim-duration: 260ms;
  --tour-anim-ease: cubic-bezier(0.22, 0.61, 0.36, 1);
}

.tour-card-shell { position: relative; overflow: hidden; }
.tour-card { will-change: transform, opacity; }
.slide-in-left { animation: slideInLeft var(--tour-anim-duration) var(--tour-anim-ease) both; }
.slide-in-right { animation: slideInRight var(--tour-anim-duration) var(--tour-anim-ease) both; }

@keyframes slideInLeft { 
  from { opacity:0; transform:translateX(calc(-1 * var(--tour-x-distance))); } 
  to { opacity:1; transform:translateX(0); } 
}

@keyframes slideInRight { 
  from { opacity:0; transform:translateX(var(--tour-x-distance)); } 
  to { opacity:1; transform:translateX(0); } 
}

@media (prefers-reduced-motion: reduce) {
  .slide-in-left, .slide-in-right { animation: none !important; }
}

:root {
  --header-h: 88px;      /* navbar height (desktop) */
  --hero-nudge: -236px;  /* move BG image UP 116px more to show full lighthouse */
  --content-shift: 40px; /* push text/CTAs stack further DOWN */
  --content-gap: 16px;   /* even rhythm between H1, subhead, CTAs, stats */
  --bottom-safe: 300px;  /* maximum space to show entire lighthouse bottom */

  /* New Design System */
