/* The hero is the stacking context container */
.hero { position: relative; }

/* Overlay is a background-only layer */
.hero-overlay{
  position: absolute;
  inset: 0;
  z-index: 0;            /* put it UNDER the content */
  pointer-events: none;  /* never intercept clicks */
}

/* All regular content sits above the overlay */
.hero > * { position: relative; z-index: 1; }

/* TOP-TOUCHING VERTICAL MIRROR — CROPPED IMAGE */
.hero::before{
  content:"";
  position:absolute; left:0; right:0; top:0;
  height: var(--header-h);
  background-image: var(--hero-img);
  background-size: cover;
  background-repeat: no-repeat;
  background-position: 100% 51.6%;  /* match the exact focal point */
  transform: scaleY(-1);
  transform-origin: top center;
  pointer-events: none; z-index: 0;

  /* soft seam into hero */
  -webkit-mask-image: linear-gradient(to bottom, #000 85%, rgba(0,0,0,0));
          mask-image: linear-gradient(to bottom, #000 85%, rgba(0,0,0,0));
}

/* Position content in middle section of lighthouse - responsive centered */
.hero-inner {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 100%;
  max-width: min(1200px, 92vw);
