/* Global safety to avoid fake off-centering */
html, body {
  width: 100%;
  overflow-x: clip;
}

.dark {
  --background: hsl(216, 50%, 12%);
  --foreground: hsl(210, 40%, 96%);
  --card: hsl(222, 35%, 16%);
  --card-foreground: hsl(210, 40%, 96%);
  --popover: hsl(216, 50%, 12%);
  --popover-foreground: hsl(210, 40%, 96%);
  --primary: hsl(15, 100%, 60%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(222, 35%, 16%);
  --secondary-foreground: hsl(210, 40%, 96%);
  --muted: hsl(217, 33%, 17%);
  --muted-foreground: hsl(215, 20%, 65%);
  --accent: hsl(222, 35%, 16%);
  --accent-foreground: hsl(210, 40%, 96%);
  --destructive: hsl(0, 84%, 60%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --border: hsl(217, 33%, 17%);
  --input: hsl(217, 33%, 17%);
  --ring: hsl(15, 100%, 60%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground;
    font-family: var(--font-sans);
  }
}

@layer components {
  /* Utility classes */
  .section--dark { background: var(--bg-0); color: var(--text); }
  .section--alt { background: var(--bg-1); color: var(--text); }
  .card { background: var(--card); border: 1px solid var(--divider); border-radius: 16px; }
  .hr, .divider { border-color: var(--divider); }

  .btn-primary { background: var(--primary); color: #061014; }
  .btn-primary:hover { background: var(--primary-600); }
  .btn-accent { background: var(--accent); color: #0B1220; }
  .btn-accent:hover { background: var(--accent-600); }
  .btn-outline-accent { background: transparent; border: 1px solid var(--accent); color: var(--accent); }
  .btn-outline-accent:hover { background: var(--accent); color: #0B1220; }

  .badge { border: 1px solid var(--divider); color: var(--muted); background: transparent; }

  .hero-glow::before {
    content: ""; 
    position: absolute; 
    inset: 0; 
    pointer-events: none;
    background:
      radial-gradient(600px 300px at 70% 0%, var(--tint-teal) 0%, transparent 60%),
      radial-gradient(500px 260px at 15% 20%, var(--tint-coral) 0%, transparent 65%);
  }

  /* Chip styles */
  .chip { border: 1px solid #2A3954; padding: .55rem .8rem; border-radius: 12px; font-weight: 500; }
  .chip--info { background: var(--tint-blue); color: #BFE0FF; }
  .chip--warn { background: var(--tint-yellow); color: #FFD38A; }
  .chip--ok { background: var(--tint-green); color: #A7F3D0; }
  .chip--violet { background: var(--tint-violet); color: #D7CDFF; }
  .chip--danger { background: var(--tint-red); color: #FCA5A5; }
  .chip--teal { background: var(--tint-teal); color: #A7F8F3; }
  .chip--muted { background: var(--tint-gray); color: #E7E5E4; }

  /* Seam-free rotational shimmer using conic gradient */
  .shimmer-gradient {
    position: relative;
    overflow: hidden;
    background: #fff;
  }

  /* seam-free rotational shimmer */
  .shimmer-gradient::before {
    content: "";
    position: absolute;
    inset: -1px;                 /* avoid edge bleed after blur */
    border-radius: inherit;
    pointer-events: none;
    z-index: 1;

    /* conic = rotationally symmetric, so no sweeping stripes */
    background: conic-gradient(
      from 0deg at 50% 50%,
      rgba(229,231,235,0.00) 0deg,
      rgba(229,231,235,0.35) 70deg,
      rgba(229,231,235,0.00) 140deg,
      rgba(229,231,235,0.35) 210deg,
      rgba(229,231,235,0.00) 280deg,
      rgba(229,231,235,0.35) 360deg   /* same as 0deg → no seam */
    );

    /* soften + fade near the border so nothing "catches" on edges */
    filter: blur(10px);
    mask: radial-gradient(150% 150% at 50% 50%, #000 60%, transparent 100%);
    mix-blend-mode: soft-light;  /* subtlety over flat opacity */

    animation: shimmer-spin 60s linear infinite;
    will-change: transform;
  }

