  }

  .text-nebula-purple {
    color: var(--nebula-purple);
  }

  .text-stellar-cyan {
    color: var(--stellar-cyan);
  }

  .text-asteroid-gray {
    color: var(--asteroid-gray);
  }

  .text-starlight-white {
    color: var(--starlight-white);
  }

  .bg-space-blue {
    background-color: var(--space-blue);
  }

  .bg-cosmic-navy {
    background-color: var(--cosmic-navy);
  }

  .bg-rocket-orange {
    background-color: var(--rocket-orange);
  }

  .bg-solar-gold {
    background-color: var(--solar-gold);
  }

  .bg-nebula-purple {
    background-color: var(--nebula-purple);
  }

  .bg-stellar-cyan {
