}

/* Smooth scrolling */
html {
  scroll-behavior: smooth;
}

/* Custom scrollbar */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: var(--space-blue);
}

::-webkit-scrollbar-thumb {
  background: var(--rocket-orange);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: var(--solar-gold);
}

/* Global safety to avoid horizontal scroll (which breaks centering) */
html, body { width:100%; overflow-x: clip; }
@supports not (overflow-x: clip){ html, body { overflow-x: hidden; } }

/* Any full-bleed wrappers should never exceed the viewport width */
#root, .min-h-screen, .page, .hero { max-width: 100%; }

/* If you have decorative layers/particles, keep them from creating overflow */
[class*="star"], [class*="particle"], [class*="float"], [class*="parallax"] {
  overflow: clip;
  max-width: 100%;
}

/* Tour card slide animations */
:root {
  --tour-x-distance: 42px;
  --tour-anim-duration: 260ms;
  --tour-anim-ease: cubic-bezier(0.22, 0.61, 0.36, 1);
}

.tour-card-shell {
  position: relative;
  overflow: hidden;
}

.tour-card {
  will-change: transform, opacity;
}

/* Outgoing animations */
.slide-out-left {
  animation: slideOutLeft var(--tour-anim-duration) var(--tour-anim-ease) both;
}

.slide-out-right {
  animation: slideOutRight var(--tour-anim-duration) var(--tour-anim-ease) both;
}

/* Incoming animations */
.slide-in-left {
  animation: slideInLeft var(--tour-anim-duration) var(--tour-anim-ease) both;
}

.slide-in-right {
  animation: slideInRight var(--tour-anim-duration) var(--tour-anim-ease) both;
}

@keyframes slideOutLeft {
  from { 
    opacity: 1; 
    transform: translateX(0); 
  }
  to { 
    opacity: 0; 
    transform: translateX(calc(-1 * var(--tour-x-distance))); 
  }
}

@keyframes slideOutRight {
  from { 
    opacity: 1; 
    transform: translateX(0); 
  }
  to { 
    opacity: 0; 
    transform: translateX(var(--tour-x-distance)); 
  }
}

@keyframes slideInLeft {
  from { 
    opacity: 0; 
    transform: translateX(calc(-1 * var(--tour-x-distance))); 
  }
  to { 
    opacity: 1; 
    transform: translateX(0); 
  }
}

@keyframes slideInRight {
  from { 
    opacity: 0; 
    transform: translateX(var(--tour-x-distance)); 
  }
  to { 
    opacity: 1; 
    transform: translateX(0); 
  }
}

/* Accessibility: honor reduced motion */
@media (prefers-reduced-motion: reduce) {
  .slide-out-left, .slide-out-right, .slide-in-left, .slide-in-right {
    animation: none !important;
  }
}

/* Availability nav — match attachment 2 */
.nav-controls {
  padding-left: 0 !important;
  padding-right: 0 !important;
}
.nav-controls .nav-btn {
  border-radius: 0 !important;
  box-shadow: none !important;
  border: 0 !important;
}
.nav-controls .nav-prev {
  background: #fff !important;
  color: #1f2937 !important;   /* gray-800 */
  margin-left: 0 !important;
}
.nav-controls .nav-prev:hover {
  background: #fff !important;
  color: #111827 !important;   /* gray-900 */
}
.nav-controls .nav-next {
  background: #0b1320 !important;  /* deep navy/black */
  color: #fff !important;
  margin-right: 0 !important;
}
.nav-controls .nav-next:hover { 
  background: #000 !important; 
}
.nav-controls .nav-btn:disabled { 
  cursor: not-allowed !important;
  pointer-events: auto !important;
} /* ensure disabled buttons show not-allowed cursor */

.nav-controls .nav-btn:disabled:hover {
  transform: none !important;
}

.nav-controls .nav-btn:not(:disabled):hover {
  transform: translateY(-0.5px);
  transition: all 0.15s ease;
}
.nav-controls .nav-btn svg { 
  width: 1rem; 
  height: 1rem; 
}

/* ===============================
   Navigation Progress Grid Setup
   =============================== */

/* Put the progress bar in the middle column of a 3-col grid */
.nav-progress-grid {
  display: grid;
  grid-template-columns: auto 1fr auto;   /* Prev | BAR | Next */
  gap: 12px;
  align-items: center;
}

/* ---- Progress ramp: equal thirds + wide blends ---- */
:root{
  /* color stops (edit colors here if you want) */
  --ramp-ok:#2b7a4b;     /* green */
  --ramp-warn:#b17800;   /* amber */
  --ramp-danger:#c3312d; /* red */

  /* equal thirds */
  --t1: 33.333%;
  --t2: 66.666%;

  /* how wide the blends are on each boundary (bigger = softer) */
  --blend: 8%;

  /* derived blend edges */
  --t1-inset:  calc(var(--t1) - var(--blend));
  --t1-outset: calc(var(--t1) + var(--blend));
  --t2-inset:  calc(var(--t2) - var(--blend));
  --t2-outset: calc(var(--t2) + var(--blend));

  /* continuous ramp with soft transitions around t1/t2 */
  --ramp-gradient: linear-gradient(
    90deg,
    /* solid green until just before first boundary */
    var(--ramp-ok) 0 var(--t1-inset),

    /* GREEN → AMBER blend zone */
    var(--ramp-warn) var(--t1-outset) var(--t2-inset),

    /* AMBER → RED blend zone */
    var(--ramp-danger) var(--t2-outset) 100%
  );

  /* feather width at the "active" end of the fill */
  --feather: 12px;  /* increase for a softer fade */
}

/* apply the ramp to the fill; keep rail light */
.progress-gradient[role="progressbar"]{
  background-color:#e5e7eb;              /* light rail */
  border:1px solid #d1d5db;
  border-radius:9999px;
}
.progress-gradient .progress-indicator{
  background:var(--ramp-gradient) !important;
  background-size:100% 100%;
  background-repeat:no-repeat;
  border-radius:inherit;
}

/* only apply feather when not full */
.progress-gradient.has-soft-cap .progress-indicator{
  -webkit-mask-image: linear-gradient(
    to right,
    #000 0,
    #000 calc(100% - var(--feather)),
    rgba(0,0,0,0) 100%
  );
          mask-image: linear-gradient(
    to right,
    #000 0,
    #000 calc(100% - var(--feather)),
    rgba(0,0,0,0) 100%
  );
}

/* remove mask when full so the end is crisp at 100% */
.progress-gradient.is-full .progress-indicator{
  -webkit-mask-image: none;
          mask-image: none;
}

/* HARD STOP: block any accidental inner content/pseudo-elements */
.progress-gradient > *:not(.progress-indicator){ display:none !important; }
.progress-gradient::before,
.progress-gradient::after,
.progress-indicator::before,
.progress-indicator::after { content:none !important; }

